function makeStructure(){
        if(process.argv.length > 2) {
           var anguCmd = process.argv[2];
            
            switch(anguCmd){
                    case 'create':
                    var appName=process.argv[3],w=process.argv[4];
                        if(!appName) {
                            console.log("Invalid appName");
                            return;
                        }
                        else{
                            console.log('creating angular app...');
                            fa.Angu.create(appName);
                        }
                                              
                        break;
                    case 'g':
                        var g=process.argv[3],w=process.argv[4];
                         if(!g || !w) {
                            console.log("Invalid instance");
                            return;
                        }
                        else{
                            fa.Angu.generate(g,w);
                                                                       
                        }
                         
                    break;
                    default:
                        console.log('Invalid Angu command');
                        break;
            }

         

            
    } else {
        console.log(" : Invalide Command");
    }

}



exports.makeApp = makeStructure;
